mapper.select:
SELECT COUNT(userEmail)
FROM USERS
WHERE userEmail = #{userEmail}

mapper.insert:
INSERT INTO USERS (
                   userEmail, userPassword, userName, userPhone, userAddr, userGender
) VALUES (
          #{userEmail},
          #{userPassword},
          #{userName},
          #{userPhone},
          #{userAddr},
          #{userGender}
                 )
